import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const CrewInfo = () => {
  const [crew, setCrew] = useState([]);
  const { id } = useParams();
  console.log(id);
  useEffect(() => {
    axios.get(`https://api.spacexdata.com/v4/crew/${id}`).then((response) => {
      console.log(response.data);
      setCrew(response.data);
    });
  }, [id]);
  return (
    <div className="container text-start" style={{ marginTop: "40px" }}>
      <div className="row">
      <div className="col-sm-6">
      <h1>crew</h1>
      <img
        src={crew.image}
        className="img-thumbnail"
         style={{ width: "450px",height:"450px" }}
      />
      </div>
      {/* <div className="col-sm-6 mx-5 my-5 text-light"> */}
      {/* <div className="row"> */}
      <div className="col-sm-6">
      <h1>{crew.name}</h1>
      <h3>Detail</h3>
      <p>agency:{crew.agency}</p>
      <p>
        Status:
        <strong
          className={crew.status === "active" ? "text-success" : "text-danger"}
        >
          {crew.status}
        </strong>
      </p>

      {/* </div> */}
      </div>
      <div className="container ">
        {/* </div> */}
        </div>
        <a href={crew.wikipedia} className="btn btn-outline-light">
          Wikipedia
        </a>
        <Link to="/crew" className="btn btn-outline-light mx-2">
          Back
        </Link>
      </div>
    </div>
  );
};

export default CrewInfo;
