import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
const SingleShips = () => {
  const { id } = useParams();
  const [data,setData] = useState([])
  useEffect(() => {
    axios.get(`https://api.spacexdata.com/v4/ships/${id}`).then((response) => {
      console.log(response.data);
      setData(response.data)
    });
  },[]);
  return (
    <div className="container my-5">
      <div className="row">
        <div className="col-sm-8 my-3">
            <img className = "img-fluid"  src = {data.image} style= {{height:"400px",width:"800px"}}/>
        </div>
        <div className= "col-sm-4 my-3">
<h1>{data.name}</h1>
<p clasName="h4">{data.home_port}</p>
        </div>
      </div>
    </div>
  );
};

export default SingleShips;
